mapper.insert:
INSERT INTO CALENDAR (CALENDAR_NO, CALENDAR_TITLE, CALENDAR_START, CALENDAR_END, ALL_DAY)
VALUES (
           (SELECT IFNULL(MAX_CALENDAR_NO + 1, 1)
            FROM (SELECT MAX(CALENDAR_NO) AS MAX_CALENDAR_NO FROM CALENDAR) AS temp),
        #{title},
        STR_TO_DATE(#{start1}, '%Y-%m-%d %H:%i:%s'),
        STR_TO_DATE(#{end}, '%Y-%m-%d %H:%i:%s'),
        #{allDay}
       );

mapper.update:
UPDATE CALENDAR SET
                    CALENDAR_TITLE = #{title} ,
                    CALENDAR_START = STR_TO_DATE(#{start1}, '%Y-%m-%d %H:%i:%s') ,
                    CALENDAR_END = STR_TO_DATE(#{end}, '%Y-%m-%d %H:%i:%s') ,
                    ALL_DAY = #{allDay}
WHERE CALENDAR_NO = #{calendarNo}
